
public class TestaOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor1=5;
		int valor2=5;
		
		if(valor1 == valor2) System.out.println("valor1 é igual a "
				+ "valor2");
		
		if(valor1 != valor2) System.out.println("valor1 é diferente "
				+ "de valor2");
		
		if(valor1 > valor2) System.out.println("valor1 é maior que"
				+ " valor2");
		
		if(valor1 < valor2) System.out.println("valor1 é menor que "
				+ "valor2");
		
		if(valor1 >= valor2) System.out.println("valor1 é maior ou "
				+ "igual a valor2");
		
		if(valor1 <= valor2) System.out.println("valor1 é menor ou "
				+ "igual a valor2");
	}

}
